mapper.update:
update users 
set imgurl=#{imgurl}, username=#{username}, callnum=#{callnum}, email=#{email} 
where userid = #{userid}

mapper.select:
select userid, pwd, imgurl, username, regdate, lastdate, logintype, callnum, email, enabled
from users
where userid = #{userid}

mapper.update:
update users
set lastdate = now()
where userid = #{userid};
